$(function(){$("#quick_search").typeahead({items:30,source:function(e,r){$.ajax({url:"ext/modules/content/navbar/store_search/content_searches.php",type:"POST",data:"query="+e,dataType:"JSON",async:!0,success:function(e){var n=e.map(function(e){var r={icon:e.icon,href:e.href,name:e.title,price:e.price};return JSON.stringify(r)});return r(n)}})},matcher:function(){return!0},sorter:function(e){for(var r,n=[],t=[],i=[];link=e.shift();){var r=JSON.parse(link);r.name.toLowerCase().indexOf(this.query.toLowerCase())?~r.name.indexOf(this.query)?t.push(JSON.stringify(r)):i.push(JSON.stringify(r)):n.push(JSON.stringify(r))}return n.concat(t,i)},highlighter:function(e){var r=JSON.parse(e);return '<table><tr><td>'+r.icon+'</td><td>'+r.name+(r.price?" ("+r.price+")":"")+'</td></tr></table>'},updater:function(e){var r=JSON.parse(e);window.location.href=r.href}})});